mapper.select:
select * from menu_relationship where 1 = 1

    and status = #{status}


    limit #{beginRow}, #{row}

select * from menu_relationship where id in

    #{id}


    and status = #{status}


    limit #{beginRow}, #{row}

mapper.insert:
INSERT INTO menu_relationship (
    gap, parent_id, child_id, id, status, create_time,
    update_time, delete_time

) VALUES (
    #{gap}, #{parent.id}, #{child.id}, #{id}, #{status}, #{createTime},
    #{updateTime}, #{deleteTime}
);

INSERT INTO menu_relationship ( gap, parent_id, child_id, id, status, create_time, update_time, delete_time
) VALUES
#{entity.gap}, #{entity.parent.id}, #{entity.child.id}, #{entity.id}, #{entity.status}, #{entity.createTime}, #{entity.updateTime}, #{entity.deleteTime}

mapper.update:
update menu_relationship set update_time = #{updateTime}

    ${key} = #{value}

where id = #{id}

mapper.select:
