mapper.select:
select * from v1_security_permission


        and #{condition.createStartTime} <= create_time and create_time < #{condition.createEndingTime}


        and #{condition.updateStartTime} <= create_time and create_time < #{condition.updateEndingTime}


        and status = #{condition.status}


        and id = #{condition.id}



    limit #{beginRow}, #{row}

select count(id) from v1_security_permission


        and #{condition.createStartTime} <= create_time and create_time < #{condition.createEndingTime}


        and #{condition.updateStartTime} <= create_time and create_time < #{condition.updateEndingTime}


        and status = #{condition.status}


        and id = #{condition.id}

mapper.update:
UPDATE v1_security_permission
SET status = #{status}, update_time = #{updateTime}, delete_time = #{deleteTime}
WHERE id IN

    #{id}
