mapper.select:
select * from security_default_user where 1 = 1

    and status = #{status}


    limit #{beginRow}, #{row}

select * from security_default_user where id in

    #{id}


    and status = #{status}


    limit #{beginRow}, #{row}

mapper.insert:
INSERT INTO security_default_user (
    id, create_time, update_time, delete_time, status,
    account, password, nickname, authority_id

) VALUES (
    #{id}, #{createTime}, #{updateTime}, #{deleteTime}, #{status},
    #{account}, #{password}, #{nickname}, #{authorityId}
);

INSERT INTO security_default_user ( id, create_time, update_time, delete_time, status, account, password, nickname, authority_id
) VALUES
#{entity.id}, #{entity.createTime}, #{entity.updateTime}, #{entity.deleteTime}, #{entity.status}, #{entity.account}, #{entity.password}, #{entity.nickname}, #{entity.authorityId}

mapper.update:
update security_default_user set update_time = #{updateTime}

    ${key} = #{value}

where id = #{id}
