mapper.select:
select * from security_permission where 1 = 1

    and status = #{status}


    limit #{beginRow}, #{row}

select * from security_permission where id in

    #{id}


    and status = #{status}


    limit #{beginRow}, #{row}

mapper.insert:
INSERT INTO security_permission (
    id, create_time, update_time, delete_time, status, name, description

) VALUES (
    #{id}, #{createTime}, #{updateTime}, #{deleteTime}, #{status}, #{name}, #{description}
);

INSERT INTO security_permission ( id, create_time, update_time, delete_time, status, name, description
) VALUES
#{entity.id}, #{entity.createTime}, #{entity.updateTime}, #{entity.deleteTime}, #{entity.status}, #{entity.name}, #{entity.description}

mapper.update:
update security_permission set update_time = #{updateTime}

    ${key} = #{value}

where id = #{id}
