mapper.select:
SELECT *
FROM v1_security_role
WHERE id IN (
SELECT role_id
FROM v1_security_user_role
WHERE authority_id = #{authorityId}

    and status = #{status}

)


    and status = #{status}

SELECT *
FROM v1_security_role
